import axios from 'axios'

const params = {
	headers: {
		Authorization: "bearer" + a736d44d8a517ef91740d9d5f3d00889acd6420cdd6bbc217b82f07e8cbb13e7c3e47d86ed8f95841d7f0301faa5e14b4b4bea28f8899fa44bff862c22792ad466ff93e4d99040ead367473ab47aff8771ab37aa19bbdf8f45da8aff2abbd5289f076643098f2815e5e92c0f463c28defe346f5836d944282a77384ae4f83fda,
	},
}

export const fetchDataFromApi = async (url) => {
	try {
		const { data } = await axios.get(process.env.REACT_APP_DEV_URL + url, params)
		return data

	} catch (error) {
		console.log(error)

		return error

	}
}
